package com.github.david04.liftutils.elem

import java.net.InetAddress
import java.util.regex.Pattern

import com.github.david04.liftutils.loc.Loc
import net.liftweb.util.Helpers._

import scala.concurrent.duration.Duration
import scala.xml.{NodeSeq, Text}


trait Validation {

  trait GtThanZero extends GenEditableIntValueElem with Loc {

    override def error: Option[NodeSeq] =
      super.error.map(Some(_)).getOrElse(
        if (getCurrentIntValue() < 0) Some(Text(loc("must-be-gt-zero"))) else None)
  }

  trait ReqStrVal extends GenEditableStringValueElem with Loc {

    override def error: Option[NodeSeq] =
      super.error.map(Some(_)).getOrElse(
        if (getCurrentStringValue() == null || getCurrentStringValue().isEmpty) Some(Text(loc("error-required"))) else None)
  }

  trait ReqStrMaxSizeVal extends GenEditableStringValueElem with Loc {

    def max: Int

    override def error: Option[NodeSeq] =
      super.error.map(Some(_)).getOrElse(
        if (getCurrentStringValue() == null || getCurrentStringValue().length > max) Some(Text(locParam("error-stringMustBeLessThan", "max" -> max.toString))) else None)
  }

  trait HostnameVal extends ReqStrVal {

    import scala.concurrent._
    import ExecutionContext.Implicits.global

    override def error: Option[NodeSeq] =
      super.error.map(Some(_)).getOrElse(
        (tryo(
          Await.result(
            future(InetAddress.getByName(getCurrentStringValue())),
            Duration(3, scala.concurrent.duration.SECONDS)))
          .map(_ => None).getOrElse(Some(Text(loc("error-invalidHostname"))))))
  }

